  </div></template><script setup lang="ts">
import { ref, computed } from 'vue'
import { useCommonStore } from '@/store/index'

defineProps({
  codeLang: String,
  code: String
})

const common = useCommonStore()
const cssVars = computed(() => {
  let vars = {
    '--vp-code-block-bg-light': '#161618',
    '--vp-c-divider': '#e2e2e3'
  }
  if (common.inverted) {
    vars['--vp-code-block-bg-light'] = '#1e1e20'
    vars['--vp-c-divider'] = 'rgba(82, 82, 89, .32)'
  }
  return vars
})

function copyStr(value: string): void {
  if (!value) return
  const execCommand = (): void => {
    const el = document.createElement('input')
    el.value = value
    document.body.appendChild(el)
    if (navigator.userAgent.match(/(iPhone|iPod|iPad);?/i)) {
      el.setSelectionRange(0, value.length)
      el.focus()
    } else {
      el.select()
    }
    if (document.execCommand('copy')) {
      console.log('复制成功')
    } else {
      console.log('复制失败')
    }
    el.blur()
    el.remove()
  }

  if (navigator.clipboard) {
    navigator.clipboard.writeText(value).then(
      function () {
        console.log('复制成功')
      },
      function () {
        execCommand()
      }
    )
  } else {
    execCommand()
  }
}

const codyMeg = ref('var(--vp-icon-copy)')
let timeout
const butCody = e => {
  // 获取父节点
  const parent = e.parentNode
  copyStr(document.querySelector(parent).querySelector('pre code').innerText)
  codyMeg.value = `var(--vp-icon-copied)`
  timeout && clearTimeout(timeout)
  timeout = setTimeout(() => {
    codyMeg.value = `var(--vp-icon-copy)`
  }, 2000)
}
</script>
